class ReportesRecursosHumanos{constructor(){this.sucursalSolicitudes=$("#sucursalPermisoReporte"),this.cargarUsuariosSolicitudes=$("#targetUsuarioPermisoReportes"),this.sucursalVacaciones=$("#sucursalVacaciones"),this.cargarUsuariosVacaciones=$("#targetUsuarioVacacionesReportes")}static listarEmpleados(s,a,o){$.ajax({type:"POST",url:ruta_server+"controllers/AjaxReportes.php",dataType:"json",data:{listaUsuarios:s,nombreCampo:o}}).done(function(s){a.html(s.html)}).fail(function(s){console.log("Ocurrio un error:",s)})}static iniciar(){let s=new ReportesRecursosHumanos;s.sucursalSolicitudes.change(function(){0!=$(this).val()?ReportesRecursosHumanos.listarEmpleados($(this).val(),s.cargarUsuariosSolicitudes,"usuario"):s.cargarUsuariosSolicitudes.html('<select class="form-control textoMay" name="usuario"><option value="0">TODOS</option></select>')}),s.sucursalVacaciones.change(function(){0!=$(this).val()?ReportesRecursosHumanos.listarEmpleados($(this).val(),s.cargarUsuariosVacaciones,"usuarioVacaciones"):s.cargarUsuariosSolicitudes.html('<select class="form-control textoMay" name="usuarioVacaciones"><option value="0">TODOS</option></select>')})}}ReportesRecursosHumanos.iniciar();